import React, { Component } from 'react'
import { DatePicker, InputNumber  } from 'antd';
import 'antd/dist/antd.css'
import './index.css'

export default class Input extends Component {
    state = {count:0}

    stockpile = () => {
        const {value1} = this.inputNumber1
        const {value2} = this.inputNumber2
        const {value3} = this.inputNumber3
        const {value4} = this.inputNumber4
        const {count} = this.state
        this.setState({count:count+value1*1})
        this.setState({count:count+value2*1})
        this.setState({count:count+value3*1})
        this.setState({count:count+value4*1})
    }

    render() {
        return (
            <div>
                <form action="">
                    选择某一天的注射点存储疫苗： <DatePicker/><br/>
                    <h4>请输入各注射点想要储存的疫苗量：</h4>
                    注射点1：<InputNumber ref={a => this.inputNumber1 = a} min={1} max={100000} defaultValue={100}/> <button onClick={this.stockpile}>存入</button> <br/>
                    注射点2：<InputNumber ref={b => this.inputNumber2 = b} min={1} max={100000} defaultValue={500}/> <button onClick={this.stockpile}>存入</button><br/>
                    注射点3：<InputNumber ref={c => this.inputNumber3 = c} min={1} max={100000} defaultValue={1000}/> <button onClick={this.stockpile}>存入</button><br/>
                    注射点4：<InputNumber ref={d => this.inputNumber4 = d} min={1} max={100000} defaultValue={1500}/> <button onClick={this.stockpile}>存入</button><br/>
                    这天总共存储了{this.state.count}支疫苗
                </form>
            </div>
        )
    }
}
