import React, { Component } from 'react'
import { DatePicker, InputNumber  } from 'antd';
import 'antd/dist/antd.css'
import './index.css'

export default class Input extends Component {
    state = {
        count:0,
        messageArr:[
            {id:'1', title:'将疫苗存入该注射点'},
            {id:'2', title:'将疫苗存入该注射点'},
            {id:'3', title:'将疫苗存入该注射点'},
            {id:'4', title:'将疫苗存入该注射点'},
        ]
    }

    stockpile = () => {
        const {value} = this.inputNumber
        const {count} = this.state
        this.setState({count:count+value*1})
    }

    render() {
        const {messageArr} = this.state
        return (
            <div>
                <form action="">
                    选择某一天的注射点存储疫苗： <DatePicker ref={c => this.input =c}/><br/>
                    <h4>请输入各注射点想要储存的疫苗量：</h4>
                    {
                        messageArr.map((msgObj) => {
                            return (
                                <frameElement>
                                    注射点{msgObj.id}：<InputNumber ref={c => this.inputNumber = c} min={1} max={100000} defaultValue={100}/> <button onClick={this.stockpile}>将疫苗存入该注射点</button> <br/>
                                </frameElement>
                            )
                        })
                    }
                    这天总共存储了{this.state.count}支疫苗
                </form>
            </div>
        )
    }
}
