import React, { Component } from 'react'
//引入store，用于获取redux中保存状态
import store from '../../redux/store'
import {createStockpileTo1Action, createStockpileTo2Action, createStockpileTo3Action, createStockpileTo4Action} from '../../redux/input_action'

import { DatePicker, InputNumber  } from 'antd';
import 'antd/dist/antd.css'

export default class Input extends Component {
    stockpileTo1 = (e) => {
        e.preventDefault()
        const {value} = this.inputNumber
		store.dispatch(createStockpileTo1Action(value*1))
    }
    stockpileTo2 = (e) => {
        e.preventDefault()
        const {value} = this.inputNumber
        store.dispatch(createStockpileTo3Action(value*1))
    }
    stockpileTo3 = (e) => {
        e.preventDefault()
        const {value} = this.inputNumber
        store.dispatch(createStockpileTo4Action(value*1))
    }
    stockpileTo4 = (e) => {
        e.preventDefault()
        const {value} = this.inputNumber
        store.dispatch(createStockpileTo2Action(value*1))
    }

    render() {
        const {count1, count2, count3, count4} = this.state
        return (
            <div>
                选择某一天的注射点存储疫苗： <DatePicker ref={c => this.input =c}/><br/>
                <h4>请输入各注射点想要储存的疫苗量：</h4>
                注射点1：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpileTo1}>存入</button>已存：{store.getState()}<br/>
                注射点2：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpileTo2}>存入</button>已存：{store.getState()}<br/>
                注射点3：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpileTo3}>存入</button>已存：{store.getState()}<br/>
                注射点4：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpileTo4}>存入</button>已存：{store.getState()}<br/>
                这天总共存储了{count1+count2+count3+count4}支疫苗
            </div>
        )
    }
}
