import React, { Component } from 'react'
import { DatePicker, InputNumber  } from 'antd';
import 'antd/dist/antd.css'
import './index.css'

export default class Input extends Component {
    state = {count:0}

    stockpile = (e) => {
        e.preventDefault()
        const {value} = this.inputNumber
        const {count} = this.state
        this.setState({count:count+value*1})
    }

    render() {
        return (
            <div>
                选择某一天的注射点存储疫苗： <DatePicker ref={c => this.input =c}/><br/>
                <h4>请输入各注射点想要储存的疫苗量：</h4>
                注射点1：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpile}>存入</button>已存：{this.state.count}<br/>
                注射点2：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpile}>存入</button>已存：{this.state.count}<br/>
                注射点3：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpile}>存入</button>已存：{this.state.count}<br/>
                注射点4：<InputNumber ref={c => this.inputNumber = c}/> <button onClick={this.stockpile}>存入</button>已存：{this.state.count}<br/>
                这天总共存储了{this.state.count}支疫苗
            </div>
        )
    }
}
